import isString from 'vanillajs-helpers/isString';
const byId = (id) => document.getElementById(id);
function findById(ids) {
    if (isString(ids)) {
        return byId(ids);
    }
    return ids.reduce((nodes, id) => {
        const node = byId(id);
        node && nodes.push(node);
        return nodes;
    }, []);
}
export default findById;
